INSERT INTO `tbl_sanpham` (`sanpham_id`, `category_id`, `sanpham_name`, `sanpham_chitiet`, `sanpham_mota`, `sanpham_gia`, `sanpham_giakhuyenmai`, `sanpham_active`, `sanpham_hot`, `sanpham_soluong`, `sanpham_image`) VALUES
(17, 5, 'Tivi LG 4K 55 Inch 55UN7000PTA', 'Loại TV: Internet Tv;\r\nKích thước TV: 55 Inch;\r\nCông Nghệ Hình Ảnh: HDR , 4K Upscaler\r\nĐộ Phân Giải: Ultra HD 4K;\r\nKích Thước (Khoảng Cách Xem): 44 - 55\" (3 - 4 mét);\r\nTổng Công Suất Loa: 20W;\r\nSố Lượng Loa: 2;\r\nHệ Điều Hành: Simple Smart (Linux);\r\nKích Thước (Có chân đế)(NxCxD): Ngang 124.4 cm - Cao 79.1 cm - Dày 28.7 cm;\r\nTrọng Lượng (Có chân đế): 13.9 Kg;\r\nHãng Sản Xuất: LG (Thương hiệu : Hàn Quốc);\r\nSản Xuất Tại: Indonesia;\r\nBảo Hành: 24 Tháng.', 'Thiết kế tinh tế cho không gian giải trí của bạn: đường viền mỏng và đường nét hiện đại đẹp mắt của LG UHD TV tăng thêm tính thẩm mỹ với thiết kế cao cấp cho không gian của bạn và nâng cao trải nghiệm xem.\r\nTV 4K đích thực đáp ứng nhu cầu giải trí của bạn: LG UHD TV là sản phẩm dành cho giải trí, đưa mọi thứ bạn xem lên một tầm cao mới. Dù là phim ảnh, thể thao hay trò chơi, sản phẩm luôn mang đến hình ảnh 4K thực sự với màu sắc sống động và độ chi tiết cao. Thưởng thức hình ảnh chân thực hơn với độ phân giải cao gấp bốn lần Full HD.\r\nBộ xử lý lõi tứ 4K: nâng cao chất lượng hình mọi nội dung bạn xem Bộ xử lý loại bỏ nhiễu trên video, tạo ra màu sắc và độ tương phản rực rỡ hơn.\r\nTrải nghiệm điện ảnh đích thực: Mang cả không gian rạp hát về nhà.\r\nDolby Vision IQ: xem mọi bộ phim một cách chân thực nhất Dolby Vision IQ điều chỉnh thông minh độ sáng màn hình, màu sắc và độ tương phản dựa trên thể loại nội dung và điều kiện ánh sáng.\r\nChiến thắng mọi trò chơi với LG UHD TV.\r\nHòa mình vào nghệ thuật âm thanh: nhiều kênh âm thanh ảo tạo ra trải nghiệm âm thanh sống động hơn. Thưởng thức cuộc đối thoại tinh tế và âm thanh tuyệt hảo như thể bạn đang ở trong chính cảnh đó.', '15400000', '8490000', 0, 0, 25, 'tivilg.jpg'),
(18, 5, 'Android Tivi 4K Sony 49 Inch KD-49X7400H', 'Loại TV: Android Tivi;\r\nKích thước TV: 49 Inch;\r\nCông Nghệ Chiếu Sáng: HDR10, HLG;\r\nĐộ Phân Giải: Ultra HD 4K;\r\nTần Số Quét: 50 Hz;\r\nKích Thước (Khoảng Cách Xem): 44 - 55\" (3 - 4 mét);\r\nTổng Công Suất Loa: 20W;\r\nSố Lượng Loa: 2;\r\nCông Nghệ Âm Thanh: Dolby Audio;\r\nHệ Điều Hành: Android;\r\nTrình Duyệt Web: Có;\r\nScreen Mirroring: Có;\r\nTính Năng Nổi Bật: Tìm kiếm giọng nói\r\nKích Thước (Có chân đế)(NxCxD): 1101 x 702 x 286 mm;\r\nTrọng Lượng (Có chân đế): 12,5 Kg;\r\nHãng Sản Xuất: Sony (Thương hiệu :Nhật Bản);\r\nSản Xuất Tại: Malaysia;\r\nBảo Hành: 24 Tháng.', 'Android Tivi 4K Sony 49 Inch KD-49X7400H thiết kế tối giản nhằm mang lại nét sang trọng, Viền mỏng màu đen, chân đế dạng tấm mỏng màu bạc.\r\nPhần cạnh viền này cũng được thiết kế khá mỏng mang lại sự hiện đại và tinh tế cần thiết cho thiết bị, nó cũng gần như sẽ trở nên vô hình khi bạn đứng ở một khoảng cách nhất định. Trước mắt người xem giờ đây sẽ chỉ còn là những hình ảnh với độ nét chuẩn 4K chứ không còn là  những đường viên đen dày bao quanh màn hình nữa. \r\nKhông chỉ \"đầu tư\" ở mặt công nghệ hình ảnh mà âm thanh cũng nhận được sự nâng cấp lớn. Hệ thống loa kép 20W kết hợp cùng bộ đôi công nghệ hàng đầu hiện nay Dolby™ Audio và DTS HD giúp cho chất lượng âm thanh được cải thiện đáng kể, đặc biệt là ở khả năng xử lý và tái tạo âm thanh vòm, biến căn phòng của bạn trở thành một rạp chiếu phim hiện đại thu nhỏ.\r\nTính năng Chromecast để chia sẻ hình ảnh, video: với tính năng này, người dùng có thể dễ dàng kết nối chiếc điện thoại và tivi với nhau để chia sẻ hình ảnh, video, game hay các ứng dụng yêu thích từ thiết bị di động sang màn hình lớn của tivi mà vẫn đảm bảo chất lượng ở mức hoàn hảo nhất.    ', '15180000', '10390000', 0, 0, 17, 'tivisony.jpg'),
(19, 5, 'Smart Tivi QLED Samsung 4K 55 inch QA55Q60T ', 'Loại tivi: Smart Tivi QLED55 inch4K;\r\nHệ điều hành: TizenOS 5.5;\r\nỨng dụng phổ biến: MyTVNetflixPops KidTrình duyệt webVieONYouTubeFPT PlayClip TVGalaxy Play (Fim+)SpotifyMP3 Zing;\r\nCông nghệ hình ảnh: Auto Motion PlusQuantum LiteQuantum DotDual LedFilm modeHDR 10+Natural mode supportQuantum HDRReal Game EnhancerSupreme UHD Dimming Nâng cấp độ tương phản - Contrast Enhancer;\r\nĐiều khiển bằng giọng nói: Google; Assistant có tiếng ViệtAlexa (Chưa có tiếng Việt)Bixby (Chưa có tiếng Việt)Tìm kiếm giọng nói trên YouTube bằng tiếng Việt\r\nRemote thông minh: One Remote;\r\nChiếu hình từ điện thoại lên TV: Airplay 2Screen MirroringTap View;\r\nKích thước: Ngang 123.01 cm - Cao 78.37 cm - Dày 24.33 cm;\r\nKhối lượng có chân: 17.4 kg;\r\nNăm ra mắt: 2020;\r\nHãng: Samsung;\r\nNơi sản xuất: Việt Nam.', 'Viền mỏng 0.5 cm tựa như không viền 3 cạnh, sang trọng và tinh tế.\r\nVới cặp chân đế hình chữ V úp ngược vững vàng trên mọi mặt phẳng, Tivi Samsung 55 inch tôn lên sự tinh tế, sang trọng cho căn phòng.\r\nSmart Tivi QLED Samsung 4K 55 inch QA55Q60T có viền mỏng 0.5 cm tựa như không viền 3 cạnh, giúp cho không gian nhà bạn một điểm nhấn sống động và nổi bật.\r\nMật độ các chấm màu gần nhau, giúp cho màu sắc của hình ảnh được hiển thị rõ ràng và trung thực khi tối ưu độ tương phản và chi tiết trong mọi điều kiện sáng. Hình ảnh hiển thị rực rỡ trong mọi điều kiện sáng bằng công nghệ Quantum HDR.\r\nCông nghệ Dual LED sử dụng 2 đèn LED với nhiệt độ màu lạnh và ấm kết hợp với nhau để kiểm soát màu sắc đèn nền tốt hơn.\r\nĐồng thời, bạn có thể hiển thị thêm thông tin cần thiết như thời tiết, tin tức, hình ảnh, thời gian,... một cách hài hòa và vô cùng nghệ thuật. Tivi Samsung sẽ ẩn đi giữa bức tường nhà bạn một cách nhẹ nhàng và tinh tế. \r\nVới tính năng Multi View, bạn vừa có thể xem được nội dung đang xem trên Smart tivi, một bên có thể xem màn hình điện thoại được chiếu lên tivi (qua tính năng Screen Mirroring đối với điện thoại Android và Airplay 2 đối với thiết bị Apple) bằng cách chia đôi màn hình tivi làm hai.', '19900000', '14900000', 0, 0, 10, 'tivisamsung.jpg'),
(20, 1, 'Laptop Lenovo Legion 5', 'CPU: Intel Core i7 Comet Lake10750H2.60 GHz;\r\nRAM: 8 GBDDR4 (2 khe)2933 MHz;\r\nỔ cứng: SSD 256 GB NVMe PCIeHDD 1 TB SATA 3;\r\nMàn hình: 15.6\"Full HD (1920 x 1080), 120Hz;\r\nCard màn hình: Card rờiNVIDIA GeForce GTX 1650 4 GB;\r\nCổng kết nối: 4 x USB 3.1HDMILAN (RJ45)USB Type-C;\r\nĐặc biệt: Có đèn bàn phím;\r\nHệ điều hành: Windows 10 Home SL;\r\nThiết kế: Vỏ nhựa;\r\nDài (mm): 362 mm;\r\nRộng (mm): 251.4 mm;\r\nDày (mm): 23.57 mm;\r\nNặng: 2.3 kg;\r\nThời điểm ra mắt: 2020.', 'Laptop Lenovo Legion 5 15IMH05 i7 (82AU0051VN) là thế hệ laptop gaming được thiết kế theo xu hướng hiện đại. Với vi xử lí đa nhân gen 10, card đồ họa rời mạnh mẽ, Lenovo Legion 5 cho trải nghiệm chiến các tựa game bom tấn cực đã.\r\nThiết kế mạnh mẽ, cứng cáp.\r\nLenovo Legion thiết kế theo phong cách gaming mạnh mẽ nhưng vẫn tạo cảm giác sang trọng. Vỏ máy được phủ sơn màu đen lịch lãm, logo Legion đặt gọn trên mặt lưng tạo điểm nhấn cho thiết kế.\r\nChiếc laptop gaming này vẫn đảm bảo tính di động khi độ dày chỉ khoảng 23.57 mm, nặng 2.3 kg, dễ dàng để đem theo bên mình và chiến game cùng bạn bè hay mang đi học, đi làm hằng ngày.', '25990000', '24000000', 0, 0, 14, 'laptoplenovo.png'),
(21, 1, 'Laptop Acer Nitro 5', 'Width (mm): 363.4;\r\nChất liệu mặt bên ngoài cùng: Nhựa;\r\nDepth (mm): 255;\r\nChất liệu khung màn hình: Nhựa;\r\nHeight (mm): 23.9;\r\nChất liệu mặt bàn phím + kê tay:  Nhựa;\r\nTrọng lượng (kg): 2.3;\r\nChất liệu mặt lưng: Nhựa;\r\nTản nhiệt: 2 quạt;\r\nP/N: NH.Q7NSV.004;\r\nNăm ra mắt: 2020;\r\nXuất xứ: Trung Quốc;\r\nThời gian bảo hành: 12 Tháng.', 'Dòng laptop gaming giá rẻ Nitro 5 của Acer đã được nâng cấp với diện mạo hoàn toàn mới. Cùng sức mạnh của bộ vi xử lý Intel thế hệ thứ 10, Acer Nitro 5 AN515 55 sẽ mang tới trải nghiệm chơi game trọn vẹn cho bạn trong tầm giá hấp dẫn.\r\nKhông chỉ cấu hình, màn hình với tần số quét lên đến 144Hz cũng là một “vũ khí” lợi hại khác của Acer Nitro 5 AN515 55. Với màn hình này, bạn sẽ thấy rõ từng chuyển động của kẻ thù, không bỏ qua bất cứ khoảnh khắc nào trong những tựa game fps diễn biến nhanh. Chất lượng hiển thị cũng hết sức tuyệt vời nhờ tấm nền 15,6 inch Full HD IPS, cho bạn những giờ phút giải trí hấp dẫn.\r\nPhiên bản mới của Acer Nitro 5 tiếp tục đi theo ngôn ngữ thiết kế gaming ấn tượng với màu đen chủ đạo, và những chi tiết nhỏ màu đỏ tạo điểm nhấn. Kiểu dáng hầm hố và góc cạnh cho bạn thêm phần cảm hứng khi chơi game. Ốp bản lề màu đỏ nổi bật trên tông màu đen sang trọng cùng 2 đường dập nổi 2 bên tạo cho Acer Nitro 5 AN515 55 sự khác biệt và đậm chất game thủ. Phần viền màn hình mỏng chỉ 6,3mm giúp laptop thêm phần nhỏ gọn, dễ dàng để bạn chơi game ở bất cứ đâu.\r\nChơi game thêm phần hiệu quả và phấn khích nhờ hệ thống đèn LED RGB 4 vùng trên Acer Nitro 5 AN515 55. Bàn phím tuyệt đẹp, phản hồi nhạy bén, tối ưu cho game thủ và các thiết lập màu sắc, hiệu ứng hấp dẫn giúp cho những tựa game của bạn thêm phần thú vị. Đồng thời ngay cả khi giải trí bằng các tác vụ như nghe nhạc hay xem phim, bàn phím RGB cũng tạo thêm nhịp điệu cho riêng bạn.\r\n\r\n\r\n', '21990000', '21490000', 0, 0, 12, 'laptopacer.jpg'),
(22, 1, 'Laptop Dell Inspiron', 'CPU: 10th Generation Intel® Core™ i3 _ 1005G1 Processor ( 1.20 GHz, 4M Cache, up to 3.40 GHz);\r\nMemory: 4GB DDr4 Bus 2666Mhz (2 Slot, 4GB x 01);\r\nHard Disk: 256GB PCIe® NVMe™ M.2 SSD (Support x 1 Slot HDD/ SSD 2.5\");\r\nVGA: Integrated Intel® UHD Graphics;\r\nDisplay: 15.6 inch Full HD (1920 x 1080) Anti Glare LED Backlit Non Touch Display\r\nOther: Webcam, SD Card Reader, USB 2.0, USB 3.1 Gen 1 Type C™, HDMI 1.4b, RJ45 (Available on select configurations), USB 3.1 Gen 1, Headphone & Microphone Audio Jack;\r\nWireless: 802.11ac + Bluetooth 4.2, 2.4 GHz;\r\nBattery: 3 Cells 42 Whrs Battery;\r\nWeight: 2,00 Kg;\r\nSoftWare: Windows 10 Home SL 64bit;\r\nXuất xứ: Brand New 100%, Hàng Phân Phối Chính Hãng, Bảo Hành Toàn Quốc;\r\nMàu sắc: Black;\r\nBảo hành: 12 tháng.', 'Dell Inspiron 3593 có thiết kế đơn giản quen thuộc của Dell. Vỏ máy được làm từ chất liệu nhựa cho cảm giác chắc chắn khi cầm trên tay. các góc máy được bo tròn nhẹ tạo sự hài hòa, thanh lịch. Máy nặng 2.28 kg, dày 22.7 mm không quá nặng để cho vào balo đem đi học, đi làm. CPU Core i3 Ice Lake thế hệ 10 cho các phản hồi nhanh chóng, hiệu năng làm việc cao hơn so với thế hệ 8 trước đó. RAM 4 GB đa nhiệm liền mạch, không giật lắc dù mở 20 tab Chrome cùng lúc.\r\nMàn hình ở Dell Inspiron 3593 kích thước 15.6 inch độ phân giải đạt chuẩn Full HD cho hình ảnh mịn màng, độ nét cao, tương phản tốt. Màn hình sử dụng tấm nền chống chói giúp giảm thiểu tình trạng bị lóa khi sử dụng laptop ở ngoài trời.\r\nLaptop Dell Inspiron 3593 (N3593C) | Intel® Core™ i3 _1005G1 _4GB _256GB SSD PCIe _VGA INTEL _Win 10 _Full HD, hướng đến khách hàng là học sinh sinh viên, nhân viên văn phòng đang tìm kiếm một chiếc máy có cấu hình đủ khỏe để giải quyết công việc cũng như giải trí hằng ngày.\r\n\r\n', '11599000', '11299000', 0, 0, 25, 'laptopdell.jpg'),
(23, 1, 'Laptop ASUS Vivobook ', 'CPU: Intel Celeron N4020;\r\nMàn hình: 15.6\" (1366 x 768);\r\nRAM: 1 x 4GB DDR4 3200MHz;\r\nĐồ họa: Intel UHD Graphics 600;\r\nLưu trữ: 256GB SSD M.2 NVMe ;\r\nHệ điều hành: Windows 10 Home SL 64-bit;\r\nPin: 2 cell 37 Wh Pin liền; Khối lượng: 1.6 kg.', 'Máy tính xách tay Asus Vivobook X515MA-BR112T (N4020) (Bạc) với thiết kế năng động: kiểu dáng gọn nhẹ chỉ 1.6kg, sắc bạc thời trang cùng màn hình viền siêu mỏng - là mẫu laptop đời mới của Asus. Dòng máy tính này thích hợp với học sinh, sinh viên hoặc nhân viên văn phòng bởi mức giá rẻ nhưng lại có chất lượng vô cùng mượt mà, tương thích cho tác vụ hằng ngày.\r\nMàn hình HD 15.6 inch viền siêu mỏng, công nghệ màn hình NanoEdge chống lóa, bảo vệ mắt.\r\nMáy tính xách tay Asus Vivobook X515MA-BR112T (N4020) (Bạc) được trang bị một màn hình nhỏ gọn với kích thước chỉ 15.6 inch HD. Bên cạnh đó, với thiết kế viền màn hình siêu mỏng, bạn sẽ có những góc nhìn rộng hơn và trải nghiệm xem thực sự ấn tượng.', '7990000', '7660000', 0, 0, 15, 'laptopasus.png'),
(24, 3, 'Máy Giặt SAMSUNG 9.0 KG WW90J54E0BW/SV', 'Loại máy giặt: Lồng ngang;\r\nKhối lượng giặt: 9.0 Kg;\r\nCông nghệ: Inverter Inverter;\r\nKhối lượng đồ giặt: Trên 8.5 Kg;\r\nĐộng Cơ: Curoa;\r\nBảng điều khiển: Điện tử + Nút xoay chọn chương trình;\r\nNgôn Ngữ: Tiếng Anh/Việt;\r\nGiặt nước nóng: Có (20-30-40-60-90 độ);\r\nTốc độ vắt: 1400 vòng/phút;\r\nCông suất: Danh định: 2000 - 2400W/Hiệu suất sử dụng điện: 22.7Wh/kg;\r\nDòng điện: 220-240V/50Hz;\r\nTrọng lượng: ~61Kg;\r\nHãng sản xuất: SAMSUNG (Thương hiệu Hàn Quốc);\r\nSản xuất tại: Việt Nam;\r\nBảo hành: 24 tháng, Nếu dùng cho hoạt động kinh doanh (nhà máy, khách sạn, giặt ủi...) thì không được bảo hành;\r\nNăm ra mắt: 2018.', 'Công nghệ giặt hơi nước Steam Cycles, Lồng giặt thiết kế kim cương, Công nghệ giặt bong bóng Eco Bubble.\r\nLồng giặt thiết kế kim cương, Khóa trẻ em. Tự động làm sạch lồng giặt, Giặt hơi nước.\r\nGiặt nhanh 15 phút, Giặt hàng ngày, Giặt siêu tiết kiệm, Đồ tối màu, Đồ len, Đồ trẻ em, Chăn ga, Giặt cotton, Giặt cotton tiết kiệm, Sợi tổng hợp, Đồ mỏng, nhẹ.\r\nLồng giặt thiết kế kim cương, Khóa trẻ em, Tự động làm sạch lồng giặt, Giặt hơi nước.', '11490000', '10990000', 0, 0, 13, 'maygiatsamsung.jpg'),
(25, 3, 'Máy Giặt Electrolux 8.0kg', 'Loại máy giặt: Lồng ngang;\r\nKhối lượng giặt: 8.0 Kg;\r\nCông nghệ: Inverter Inverter;\r\nKhối lượng đồ giặt: Từ 7.5 Kg - 8.5 Kg;\r\nĐộng Cơ: Curoa;\r\nCông nghệ giặt: Giặt hơi nước Vapour, Lồng giặt HIVE hình tổ ong, Thêm đồ trong khi giặt, Giặt nước nóng, Khóa trẻ em, Chế độ trì hoãn khởi động;\r\nTốc độ vắt: 1200 vòng/phút;\r\nCông suất: Tối đa: 2000W;\r\nDòng điện: 220V-240V/50Hz;\r\nKích thước (R*S*C): 600 x 568 x 850mm;\r\nHãng sản xuất: Electrolux (Thương hiệu: Thụy Điển);\r\nSản xuất tại: Thái Lan.\r\n', 'Giặt hơi nước Vapour, Lồng giặt HIVE hình tổ ong, Thêm đồ trong khi giặt, Giặt nước nóng, Khóa trẻ em, Chế độ trì hoãn khởi động.\r\nCông nghệ giặt bằng hơi nước ở giai đoạn giặt cuối cùng giúp giảm thiểu các vết nhăn trên quần áo lên đến 22% và giúp quần áo phẳng phiu. Ở nhiệt độ 40C, máy giặt giúp loại bỏ 99.9% tác nhân gây dị ứng và vi khuẩn từ bụi bẩn hay vật nuôi. Bạn không những thấy rõ mà còn cảm nhận được sự tuyệt vời này. \r\nĐồ len cao cấp của bạn sẽ được giặt một cách an toàn với chức năng giặt đồ len đã được cấp chứng nhận Woolmark. Chức năng chuyên dụng này giúp đạt được hiệu quả làm sạch đồ len tối ưu mà vẫn không gây bết khi giặt, nhờ vậy áo quần của bạn luôn mới.\r\n\r\n', '9490000', '8070000', 0, 0, 15, 'maygiatelectrolux.png'),
(26, 3, 'Máy giặt Sharp 11.0kg', 'Loại máy giặt: Cửa trên;\r\nLồng giặt: Lồng đứng;\r\nKhối lượng giặt: 11 kg;\r\nCông nghệ Inverter: Không;\r\nKiểu động cơ: Truyền động dây Curoa;\r\nTốc độ quay vắt: 730 vòng/phút;\r\nHiệu suất sử dụng điện: 9.7 Wh/kg;\r\nKích thước: Cao 103.1 cm - Ngang 62 cm - Sâu 59.5 cm;\r\nKhối lương: Nặng 37 kg;\r\nXuất xứ: Indonesia.', 'Màu xám đen, máy giặt Sharp ES-W82GV-H có thiết kế lồng đứng truyền thống, gam màu xám đen với nắp kính cường lực viền nhựa đen bóng sang trọng, dễ dàng phối với nội thất.\r\nMáy giặt có khối lượng 11.0 kg, phù hợp cho gia đình có từ 5-7 thành viên hoặc ít người hơn nhưng có nhu cầu giặt 1 lần nhiều đồ.', '7990000', '7290000', 0, 0, 10, 'maygiatsharp11.jpg'),
(27, 3, 'Máy giặt Sharp 8.2kg', 'Kiểu máy giặt: Cửa trên;\r\nKiểu lồng giặt: Lồng ngang;\r\nKhối lượng giặt: 8.2kg;\r\nTốc độ quay vắt (vòng/ phút): 900 vòng/phút;\r\nTruyền động: Bằng dây Curoa;\r\nCông suất (W): Đang cập nhật;\r\nXuất xứ: Indonesia.', 'Màu xám sáng, máy giặt Sharp ES-W82GV-H có thiết kế lồng đứng truyền thống, gam màu xám sáng với nắp kính cường lực viền nhựa đen bóng sang trọng, dễ dàng phối với nội thất.\r\nMáy giặt có khối lượng 8.2 kg, phù hợp cho gia đình có từ 3 - 4 thành viên hoặc ít người hơn nhưng có nhu cầu giặt 1 lần nhiều đồ.', '5490000', '4990000', 0, 0, 10, 'maygiatsharp8.2.png'),
(28, 5, 'Tivi 4K TCL 65 Inch 65P618', 'Loại TV: Smart LED;\r\nKích thước TV: 65 inch;\r\nCông Nghệ Hình Ảnh: HDR10, Micro Dimming, Sắc nét 4K UHD,Sports mode;\r\nĐộ Phân Giải: Ultra HD 4K;\r\nKích Thước (Khoảng Cách Xem): Trên 55\" (4-7 mét);\r\nTổng Công Suất Loa: 20W;\r\nSố Lượng Loa: 2;\r\nCông Nghệ Âm Thanh: Dolby Atmos;\r\nHệ Điều Hành: Android 9.0;\r\nTrình Duyệt Web: Có;\r\nScreen Mirroring: Có;\r\nKết nối bàn phím, chuột: Có;\r\nTính Năng Nổi Bật: Có Bluetooth;\r\nKích Thước (Có chân đế)(NxCxD): 1457 x 897 x 320 mm;\r\nKích Thước (Không chân đế)(NxCxD): 1457 x 838 x 83.5 mm;\r\nHãng Sản Xuất: TCL;\r\nSản Xuất Tại: Việt Nam;\r\nBảo Hành: 36 Tháng.', 'Android Tivi 4K TCL 65 Inch 65P618 sở hữu đường nét được thiết kế thanh mảnh, đơn giản cùng gam màu đen sang trọng, sẽ tạo điểm nhấn cho không gian nội thất gia đình của bạn. Màn hình tivi TCL 43 inch phù hợp với những không gian vừa như: Phòng khách, phòng học, phòng họp,...  \r\nĐộ phân giải 4K cho hình ảnh sắc nét gấp 4 lần độ phân giải Full HD.\r\nHình ảnh được tăng cường độ tương phản nhờ công nghệ HDR10: công nghệ HDR10 tăng cường khả năng làm phong phú màu sắc, tối ưu từ điểm tối nhất tới điểm sáng nhất tạo độ chi tiết màu sắc của từng điểm ảnh.\r\nCông nghệ Micro Dimming: giúp chia màn hình tivi thành 1296 vùng riêng biệt, điều chỉnh độ sáng và bóng tối một cách thông minh. Chất lượng hình ảnh được tối ưu hóa, cho trải nghiệm xem vượt trội, đặc biệt là trong bóng tối.\r\nCông Nghệ Âm Thanh sống động: thưởng thức âm thanh cực kỳ chân thực và sống động với loa cùng công nghệ Dolby Audio. Công nghệ âm thanh này sẽ cho bạn trải nghiệm các thước phim, những chương trình bóng đá với lời thoại cực kỳ chân thật và sắc nét.\r\nGiao diện dễ sử dụng, kho ứng dụng phong phú với hệ điều hành Android 9.0.\r\nVới trí tuệ nhân tạo AI-IN, bạn có thể điều khiển các thiết bị thông minh trong nhà dễ dàng với tivi như bật tắt đèn, ra lệnh cho robot hút bụi làm việc, chỉnh nhiệt độ phòng,... điều khiển từ xa dễ dàng.', '21490000', '15290000', 0, 0, 12, 'tivitcl.jpg'),
(29, 2, 'Tủ Lạnh Panasonic Inverter 188 lít NR-BA229PAVN', 'Loại tủ: Ngăn đá trên;\r\nDung tích thực: 188 lít;\r\nMàu sắc: Xanh;\r\nĐóng tuyết: Không đóng tuyết;\r\nCông nghệ tiết kiệm điện: Inverter;\r\nChỉnh nhiệt độ: Thủ công;\r\nLàm lạnh nhanh: Có;\r\nLấy đá ngoài: Không;\r\nDung tích: Từ 150-300 lít;\r\nLấy nước ngoài: Không;\r\nCông nghệ làm lạnh: Panorama;\r\nTính năng khác: Công nghệ kháng khuẩn -Ag Clean với tinh thể bạc Ag+ , inverter tiết kiệm điện;\r\nNgăn đá (ngăn đông): 53 lít;\r\nNgăn rau quả: 135 lít;\r\nKhay đá: Có;\r\nChất liệu khay ngăn: Kính chịu lực;\r\nNgăn đựng chai lọ: Có;\r\nCông suất tiêu thụ	: ~ 0.96 kW/ngày;\r\nChất liệu cửa tủ lạnh: Kim loại phủ sơn tĩnh điện;\r\nDòng điện: 220V/50Hz/1.5A;\r\nTrọng lượng: 35 kg;\r\nLoại Gas làm lạnh: R600a;\r\nKích thước: 526 x 584 x 1393mm;\r\nHãng sản xuất: Panasonic (Thương Hiệu: Nhật Bản );\r\nSản xuất tại: Việt Nam;\r\nBảo hành: 24 tháng;\r\nNăm ra mắt: 2020.', 'Thiết kế sang trọng, lớp phủ sơn tĩnh điện bền bỉ, đẹp mắt: tủ Lạnh Panasonic Inverter 188 lít NR-BA229PAVN thuộc kiểu tủ ngăn đá trên truyền thống nhưng vẫn sang trọng với lớp phủ sơn tĩnh điện màu xanh đẹp mắt, bền bỉ ắt hẳn sẽ làm bừng sáng không gian căn bếp nhà bạn.\r\nTủ lạnh Panasonic Inverter sử dụng công nghệ Ag Clean có khả năng giải phóng các ion Ag+ giúp kháng khuẩn và khử mùi hôi từ thực phẩm một cách hiệu quả, trả lại bầu không khí tươi mát, cho thực phẩm được thơm ngon, trọn vị.\r\nĐược tích hợp cảm biến Econavi, tủ lạnh sẽ cảm nhận nhiệt độ không gian bên ngoài, nhiệt độ lạnh bên trong, tần suất mở cửa tủ, ánh sáng xung quanh và khối lượng thực phẩm được lưu trữ, để tối ưu hóa năng lượng điện mà tủ lạnh sử dụng, duy trì mức làm mát lý tưởng.\r\nNhờ công nghệ làm lạnh Panorama, hơi lạnh sẽ được thổi nhanh và lan tỏa đều khắp tủ, giúp thực phẩm được làm lạnh toàn diện chỉ trong khoảng thời gian ngắn.\r\nCác khay kệ bên trong tủ lạnh làm bằng kính chịu lực, cho phép bạn thoải mái chứa nhiều thực phẩm mà không lo bị hỏng kệ tủ. Hơn nữa, các khay kính rất dễ lau chùi và vệ sinh trong quá trình sử dụng.\r\n', '7290000', '5790000', 0, 0, 25, 'tulanhpanasonic.jpg'),
(30, 2, 'Tủ Lạnh HITACHI Inverter 406 Lít R-FG510PGV8', 'Loại tủ: Ngăn đá trên;\r\nDung tích tổng: 406 Lít;\r\nĐóng tuyết: Không đóng tuyết;\r\nCông nghệ tiết kiệm điện: Inverter;\r\nChỉnh nhiệt độ: Thủ công;\r\nLàm lạnh nhanh: Có;\r\nLấy đá ngoài: Không;\r\nDung tích: Từ 300-450 Lít;\r\nLấy nước ngoài: Không;\r\nLàm đá tự động: Không;\r\nTính năng khác: Hệ thống quạt kép giúp làm lạnh độc lập và hiệu quả cho cả hai ngăn, ngăn lạnh và ngăn đá, không như những tủ lạnh thông thường khác chỉ có một quạt. Hệ thống quạt kép cải tiến mới mang đến hiệu quả tiết kiệm điện năng tốt hơn và làm lạnh cực mạnh;\r\nNgăn đá (ngăn đông): Có;\r\nNgăn rau quả: Có;\r\nChất liệu khay ngăn: Kính chịu lực;\r\nNgăn đựng chai lọ: Có;\r\nCông suất tiêu thụ: 110 W;\r\nChất liệu cửa tủ lạnh: Thiết kế mặt trước kính;\r\nDòng điện: 220V/50Hz/1.5A;\r\nTrọng lượng: 69 Kg;\r\nLoại Gas làm lạnh: R-600a;\r\nKích thước: 680x720x1770(RxSxC) mm;\r\nHãng sản xuất: Hitachi (Thương Hiệu: Nhật Bản);\r\nSản xuất tại: Thái Lan; Bảo hành: 12 tháng.', 'Tủ Lạnh HITACHI Inverter 406 Lít R-FG510PGV8(GBW) Với vẻ ngoài sang trọng và đẳng cấp, tủ lạnh Hitachi sẽ tạo nên sự hiện đại cùng mới mẻ cho căn bếp của bạn.\r\nMáy Nén Inverter Hiệu Suất Cao: mang đến khả năng làm lạnh cực mạnh nhờ tạo ra lượng lớn không khí lạnh, đồng thời hoạt động hiệu quả khi làm lạnh ở chế độ năng lượng thấp nhờ điều khiển điện tử.\r\nHệ thống quạt kép: giúp làm lạnh độc lập và hiệu quả cho cả hai ngăn, ngăn lạnh và ngăn đá, không như những tủ lạnh thông thường khác chỉ có một quạt. Hệ thống quạt kép cải tiến mới mang đến hiệu quả tiết kiệm điện năng tốt hơn và làm lạnh cực mạnh, hỗ trợ bởi Bộ đôi Cảm biến nhiệt Eco được trang bị cho từng ngăn, giúp nhận biết sự thay đổi nhiệt độ tại mọi thời điểm.', '15490000', '11290000', 0, 0, 21, 'tulanhhatachi.jpg'),
(31, 2, 'Tủ Lạnh Samsung Inverter 208 lít RT20HAR8DBU/SV', 'Loại tủ: Ngăn đá trên;\r\nDung tích thực: 208 lít;\r\nMàu sắc	Giả gương: màu đen;\r\nĐóng tuyết: Không đóng tuyết;\r\nCông nghệ tiết kiệm điện: Inverter;\r\nChỉnh nhiệt độ: Thủ công;\r\nGiữ lạnh khi cúp điện: Có;\r\nLàm lạnh nhanh: Có;\r\nLấy đá ngoài: Không;\r\nDung tích: Từ 150-300 lít;\r\nLấy nước ngoài: Không;\r\nCông nghệ làm lạnh: Làm lạnh vòm All -Around Cooling;\r\nTính năng khác: Bảo quản thịt cá không cần rã đông, Inverter tiết kiệm điện;\r\nNgăn đá (ngăn đông): 53 lít;\r\nNgăn rau quả: 183 lít;\r\nKhay đá: Có;\r\nChất liệu khay ngăn: Kính chịu lực;\r\nNgăn đựng chai lọ: Có;\r\nCông suất tiêu thụ: ~ 0.93 kW/ngày;\r\nChất liệu cửa tủ lạnh: Kim loại phủ sơn tĩnh điện;\r\nDòng điện: 220V/50Hz/1.5A;\r\nTrọng lượng: 44.5 kg;\r\nKích thước: 1445x 637x 555 mm;\r\nHãng sản xuất: Samsung (Thương Hiệu: Hàn Quốc);\r\nSản xuất tại: Trung Quốc;\r\nBảo hành: 24 tháng;\r\nNăm ra mắt: 2020.', 'Công Nghệ Digital Inverter tự động điều chỉnh tốc độ máy nén tùy theo nhu cầu làm lạnh với 7 cấp độ tuỳ chỉnh. Nhờ đó, giúp tiết kiệm năng lượng, giảm độ ồn, giảm hao mòn và tăng tuổi thọ máy. Đồng thời, chế độ bảo hành 10 năm giúp bạn luôn an tâm sử dụng tủ lạnh để mang đến những bữa ăn ngon cho cả nhà.\r\nNgăn giữ ẩm tối ưu kiểm soát sự lưu thông không khí để duy trì độ ẩm tốt nhất, bảo quản hiệu quả các loại thực phẩm dễ hỏng, giữ thức ăn tươi ngon lâu hơn. Khi độ ẩm xuống quá thấp, ngăn giữ ẩm sẽ được khóa lại, hạn chế hơi ẩm thoát ra ngoài. Khi độ ẩm lên quá cao, ngăn giữ ẩm sẽ thoát bớt khí để duy trì độ ẩm.\r\nKhay Kéo Linh Hoạt: việc lấy thực phẩm trong tủ lạnh đôi khi gặp trở ngại, nhưng với Khay Linh Hoạt được đặt trên thanh trượt và kéo ra dễ dàng, bạn có thể lưu trữ, sắp xếp gọn gàng và lấy thức ăn nhanh chóng; đồng thời tiếp cận được những thực phẩm ở sâu bên trong.\r\nBộ lọc than hoạt tính Deodorizer trên tủ lạnh Samsung Inverter 208 lít RT20HAR8DBU/SV giúp loại bỏ hoàn toàn những mùi hôi khó chiu, các nấm mốc và vi khuẩn phát sinh bên trong tủ, để không khí trong tủ lạnh luôn tươi mát và thực phẩm trọn vị tươi ngon.', '7190000', '5740000', 0, 0, 20, 'tulanhsamsung.jpg'),
(32, 2, 'Tủ Lạnh TOSHIBA Inverter 180 Lít GR-B22VU', 'Loại tủ: Ngăn đá trên;\r\nDung tích tổng: 180 Lít;\r\nĐóng tuyết: Không đóng tuyết;\r\nCông nghệ tiết kiệm điện: Inverter;\r\nChỉnh nhiệt độ: Thủ công;\r\nLàm lạnh nhanh: Có;\r\nLấy đá ngoài: Không;\r\nDung tích: Từ 150-300 lít;\r\nLấy nước ngoài: Không;\r\nLàm đá tự động: Không;\r\nTính năng khác: Cấp đông mềm bảo quản thực phẩm không cần rã đông, Ngăn cấp đông mềm Ultra Cooling Zone -1°C;\r\nCông suất tiêu thụ	: ~ 0.89 kW/ngày;\r\nChất liệu cửa tủ lạnh: Thiết kế mặt trước kính;\r\nDòng điện: 220V/50Hz/1.5A;\r\nTrọng lượng: 37 Kg;\r\nKích thước: 545 x 623 x 1285(RxSxC) mm;\r\n Hãng sản xuất: Toshiba (Thương Hiệu: Nhật Bản);\r\n Sản xuất tại: Thái Lan;\r\n Bảo hành: 24 tháng;\r\n Năm ra mắt: 2019.', 'Màu sắc sang trọng, thiết kế nhỏ gọn: với kiểu tủ ngăn đá trên, chiếc tủ lạnh Toshiba Inverter 180 lít GR-B22VU(UKG) được thiết kế nhỏ gọn nhưng vẫn toát lên vẻ đẹp hiện đại bởi tông màu đen tuyền sang trọng. Dễ dàng đặt ở bất kì vị trí nào trong nhà, từ phòng khách đến phòng bếp .\r\nChiếc tủ lạnh Toshiba này chỉ sở hữu dung tích 180 lít, phù hợp với gia đình nhỏ tầm 2 – 3 người nhưng sẽ là nội thất bắt mắt trong không gian nhà bạn. \r\nNhờ có sự hỗ trợ của công nghệ biến tần Inverter, tủ lạnh Toshiba GR-B22VU UKG sẽ giúp bạn bớt lo lắng về tiền điện mỗi tháng, vì mang lại khả năng tiết điện điện hiệu quả so với tủ lạnh thông thường.   Công nghệ biến tần này sẽ kiểm soát sự hoạt động của máy nén giúp duy trì nhiệt độ bên trong tủ và hạn chế tiếng ồn đến mức tối đa.  \r\nCông nghệ làm lạnh thực phẩm bằng luồng khí vòng cung giúp hơi lạnh được lưu chuyển đến mọi ngóc ngách trong tủ lạnh cũng như khắc phục được tình trạng thực phẩm bị đông đá (do quá lạnh) hay bị hư hao (do thiếu độ lạnh).  \r\nHệ thống màng lọc Ag+ Bio của tủ sẽ kháng khuẩn và khử mùi tối đa các loại thực phẩm có mùi khó chịu nhất như thịt và cá. Đồng thời, bộ lọc sinh học góc Aldehyde sẽ hỗ trợ việc khử mùi cực mạnh thuộc nhóm thực phẩm lên men (như nước tương, rượu,…) mà trước đây không thể khử được.  \r\nChiếc tủ lạnh Toshiba này sẽ trang bị thêm ngăn cấp đông mềm Ultra Cooling Zone cực kì hữu dụng cho các chị em nội chợ. Không cần phải tốn thời gian chờ rã đông, bạn có thể lấy thực phẩm tươi sống ra chế biến ngay mà vẫn đảm bảo được chất lượng món ăn.   Tuy nhiên ngăn này chỉ giúp lưu trữ thực phẩm tươi dùng trong ngày thôi, nên nếu muốn trữ lâu hơn, bạn nên dùng ngăn trữ đông nhé!  \r\nNgăn chứa lớn cho phép bạn có thể dự trữ rau củ nhiều mà vẫn yên tâm về mức độ tươi ngon của thực phẩm. Ngoài ra, ngăn chứa có khả năng giữ ẩm tốt nên đảm bảo cho việc bảo quản thực phẩm được kéo dài.  ', '5690000', '5490000', 0, 0, 30, 'tulanhtoshiba.jpg'),
(33, 6, 'Nồi Cơm Điện Tử CUCKOO CR-0631F ', 'Loại Sản Phẩm: Nồi Cơm Điện Tử;\r\nModel: CR-0631F-1LÍT;\r\nDung tích thực: 1.0 Lít;\r\nDung tích: 1 - 1.5 Lít;\r\nCông nghệ: 3 mâm nhiệt;\r\nLòng Nồi: Chống Dính;\r\nChức năng giữ ấm: Có;\r\nChế độ nấu: Đa chế độ nấu;\r\nHẹn giờ nấu: Có;\r\nPhím điều khiển: Nút nhấn điện tử; Màn hình hiển thị: LCD;\r\nĐèn báo: Không;\r\nLoại nắp: Nắp liền;\r\nKích thước: 317 x 235 x 213;\r\nTrọng lượng: 3.6 Kg;\r\nMàu sắc: Trắng + Hồng;\r\nChất liệu: Vỏ ngoài bằng nhựa / Ruột nồi áp dụng công nghệ pủ \"Black Gold\";\r\nĐiện áp: 220V- 50hz;\r\nCông suất: 580W;\r\nHãng Sản Xuất: CUCKOO ( Thương -Hiệu Hàn Quốc );\r\nSản Xuất Tại: Hàn Quốc;\r\nBảo hành(trực tiếp tại siêu thị): 24 Tháng.', 'Thiết kế sang trọng, thân nồi dày giúp giữ nhiệt lâu hơn: nồi cơm điện tử Cuckoo CR- 0631F có kiểu dáng chắc chắn, di chuyển, bảo quản dễ dàng, thuận tiện. Thân nồi giữ cho cơm, thức ăn nóng trong thời gian dài ngay cả khi nồi không kết nối điện.\r\nDung tích 1 lít, đáp ứng nhu cầu cơm hằng ngày cho gia đình 2 - 4 người.\r\nBa mâm nhiệt giúp cơm được nấu chín nhanh, ít tốn điện, tiết kiệm thời gian.\r\nLòng nồi bằng gang tráng men chống dính, nấu cơm tơi, ngon: độ bền cao, an toàn với sức khỏe người dùng, vệ sinh đơn giản sau khi sử dụng.\r\nNắp thiết kế đặc biệt giúp cơm nấu không bị nhão: lớp trong của nắp có thể tháo ra tiện vệ sinh.\r\nĐiều khiển điện tử hiện đại và màn hình hiển thị LED cỡ lớn, dễ theo dõi: giúp bạn thao tác, tùy chỉnh các chức năng, chế độ chính xác, tiện lợi.\r\nNồi cơm điện tử Cuckoo CR- 0631F thiết lập đến 6 chế độ nấu tự động là nấu cơm trắng “Glutinuos”; nấu gạo mầm “Gaba”; nấu gạo hạt dài hoặc gạo lứt “Mixed/Brown”; nấu cháo “Porridge”; hấp “Steam”; nấu súp, nấu cháo hoặc để sắc thuốc bắc ở nhiệt độ thấp “Slow cook”, tùy theo nhu cầu sử dụng mà chọn chọn chế độ nấu phù hợp.', '2989000', '2489000', 0, 0, 30, 'noicomdiencuckoo.jpg'),
(34, 6, 'Nồi cơm điện TOSHIBA RC-18NTFV', 'Loại Sản Phẩm: Nồi Cơm Điện;\r\nModel: RC-18NTFV(W);\r\nDung tích thực: 1.8 Lít;\r\nDung tích: 1.6 - 2 Lít;\r\nCông nghệ: Hệ thống nhiệt 3D truyền nhiệt đồng nhất;\r\nLòng Nồi: Hợp kim nhôm tráng lớp Titanium;\r\nChức năng giữ ấm: Có;\r\nChế độ nấu: Đa dạng;\r\nHẹn giờ nấu: Có;\r\nMàn hình hiển thị: LCD;\r\nLoại nắp: Nắp thoát hơi lớn, có thể tháo rời\r\nPhụ kiện: Tùy theo năm sản xuất;\r\nMàu sắc: Tùy theo năm sản xuất;\r\nĐiện áp: 220V- 50hz;\r\nCông suất: 800w;\r\nHãng Sản Xuất: TOSHIBA;\r\nBảo hành(trực tiếp tại siêu thị): 12 tháng.', 'Thiết kế lòng nồi dày 4 mm: nồi cơm điện tử Toshiba 1.8 lít RC-18NTFV (W) với lòng nồi được làm từ chất liệu hợp kim nhôm tráng lớp Titanium chống trầy và Fluorocarbon Polymer chống dính, với độ dày lên đến 4 mm, cho khả năng chống dính và dẫn nhiệt cao, giúp cơm vừa chín đều, vừa không bị dính vào lòng nồi sau khi nấu.\r\nGiữ trọn hương vị thơm ngon cho cơm: nhờ áp dụng công nghệ tiên tiến, nồi cơm điện tử Toshiba 1.8 lít RC-18NTFV (W) tích hợp chương trình nấu tự động thông minh, có khả năng tự điều chỉnh nhiệt độ sao cho phù hợp nhất với từng giai đoạn của quá trình nấu, từ đó giúp cơm chín đều, chín nhanh mà vẫn giữ được dưỡng chất cũng như hương vị thơm ngon vốn có của hạt gạo.', '2990000', '2590000', 0, 0, 25, 'noicomdientoshiba.jpg'),
(35, 6, 'Nồi cơm điện tử 1.8L Sunhouse mama SHD8903', 'Loại Sản Phẩm: Nồi Cơm Điện Tử;\r\nModel: SHD8903;\r\nDung tích thực: 1.8 Lít;\r\nDung tích: 1.6 - 2 Lít;\r\nCông nghệ: 3D;\r\nLòng Nồi: Chống Dính;;\r\nChế độ nấu: Đa dạng;\r\nHẹn giờ nấu: Có\r\nMàn hình hiển thị: Có;;\r\nChất liệu: Hợp kim tráng men chống dính Whitford;\r\nĐiện áp: 220V- 50hz;\r\nCông suất: 860W;\r\nHãng Sản Xuất: SUN HOUSE;\r\nSản Xuất Tại: Trung Quốc;\r\nBảo hành(trực tiếp tại siêu thị): 12 tháng.', 'Nồi cơm điện tử 1.8L Sunhouse mama SHD8903 được thiết kế gọn gàng, vỏ nồi inox bóng đẹp, nổi bật, màu ghi đen sang trọng sẽ mang đến nét sáng sủa, hiện đại cho không gian bếp nhà bạn. Đặc biệt vỏ nồi dày bền, giữ ấm thức ăn lâu, hạn chế trầy xước, ít bám bẩn, giúp bạn vệ sinh đơn giản, dễ dàng. Dung tích lên đến 1.8 lít của nồi phù hợp cho gia đình 4 – 6 thành viên.\r\nLòng nồi dày 2.8mm giữ nhiệt tốt: lòng nồi hợp kim nhôm dày tới 2.8mm giúp dẫn nhiệt và giữ nhiệt tốt, cho hạt cơm ngon, mềm dẻo, chín đều hơn và không bị cháy khét như lòng nồi mỏng thông thường. Ngoài ra, lòng nồi còn phủ chống dính đến từ thương hiệu nổi tiếng Whitford (USA) chịu được nhiệt độ cao, không chứa các độc tố gây hại, an toàn tuyệt đối cho sức khỏe.\r\nĐa chức năng: Nồi có nhiều chế độ nấu khác nhau tùy theo khẩu vị như mềm, dẻo, cơm niêu và nấu tiêu chuẩn hoặc tùy theo nhu cầu như nấu nhanh, nấu chậm, nấu tiêu chuẩn. Bạn cũng có thể dùng nồi để nấu cháo nguyên hạt hay cháo nhừ, nấu súp, làm bánh, hấp, hâm nóng... rất tiện lợi, dễ dàng.\r\nCông nghệ ủ ấm ba chiều: Với công nghệ ủ ấm 3 chiều và cấu tạo 3 mâm nhiệt, cho nhiệt lượng tỏa rộng, đều giúp cơm nấu chín ngon, hạn chế tình trạng cơm nhão, khô, sống. Sau khi cơm chín, nhiệt lượng còn giữ lại trong nồi, bảo toàn nguyên vẹn hương vị, chất dinh dưỡng tới 48 tiếng, giúp gia đình bạn luôn có cơm ngon để thưởng thức mọi lúc.\r\nHẹn giờ tự động: Nồi có chức hẹn giờ nấu tự động giúp tiết kiệm thời gian nấu nướng và rất thích hợp với những người bận rộn.\r\nPhím bấm cảm ứng, màn hình LED hiện đại: hệ thống phím nhấn cảm ứng và màn hình LED hiện đại, các tính năng được thể hiện song ngữ Việt/Anh giúp việc điều chỉnh các chế độ trở nên dễ dàng, đơn giản.\r\nVan thoát hơi thông minh: nồi cơm điện tử Sunhouse có trang bị van thoát hơi thông minh, tự động cảm nhận và kiểm soát lượng hơi nước bốc lên để cơm không bị quá khô hoặc quá nhão, chống tràn hiệu quả. Van có thể tháo rời, người dùng nên thường xuyên vệ sinh để van khô ráo, sạch sẽ, hạn chế vi khuẩn sinh sôi, phát triển xâm nhập vào cơm gây hại cho sức khỏe.', '1590000', '1190000', 0, 0, 32, 'noicomdiensunhouse.png'),
(36, 6, 'Nồi cơm điện nắp rời Sharp 2.8L', 'Bảng điều khiển: Cơ;\r\nDung tích: 2,8 lít;\r\nThời gian giữ nóng: 5 giờ;\r\nSố người ăn: 2.6 - 5 lít (8 - 15 người);\r\nCông suất: 1.000W;\r\nNguồn điện áp: 220V - 240V / 50Hz - 60Hz;\r\nKích thước: 367mm x 304mm x 289mm;\r\nTrọng lượng sản phẩm: 3,4kg;\r\nThương hiệu: Nhật Bản;\r\nSản xuất tại: Thái Lan;\r\nBảo hành: 12 tháng.', 'Nồi cơm điện nắp rời Sharp KSH-228V (KSH-D28V) - 2.8L nấu cơm nhanh nhờ mâm nhiệt lớn: nồi cơm điện nắp rời Sharp KSH-228V có chất liệu lòng nồi bằng hợp kim nhôm cao cấp, tăng độ bền cho sản phẩm. Thành nồi dày cho độ truyền nhiệt cao, nấu cơm nhanh, cơm chín đều và ngon hơn. Nồi cơm điện Sharp KSH-228V có dung tích 2,8 lít, phù hợp cho gia đình đông người hay quán ăn, tiệm cơm.\r\nNắp nồi bằng inox cao cấp, an toàn cho sức khỏe, thiết kế nắp hình cầu nên sẽ không đọng nước nơi bề mặt nắp, tránh tình trạng nước nhiễu xuống làm cơm nhão, không ngon, nắp có thể tháo rời dễ dàng vệ sinh sau khi sử dụng.\r\nNgoài ra, nồi cơm điện Sharp có mâm nhiệt lớn, phẳng và đều, công suất 1.000W giúp nhiệt lượng được tản đều đến lòng nồi và ngay cả trên thành nồi, giúp cơm không chỉ chín mặt đáy mà còn chín đều trên bề mặt trên.\r\nTay cầm nắp bằng nhựa tăng khả năng cách nhiệt, dễ dàng cầm nắm di chuyển mà không bị bỏng. Dây điện dài 1.5m ± 0.1m tiện sử dụng khi ổ cắm xa, dây tháo thời được tiện cất trữ và bảo quản.\r\nNồi hoạt động đơn giản với một nút bấm, chỉ cần bật nút xuống và đợi từ 20 - 30 phút là có được bữa cơm nóng hổi mà không tốn quá nhiều công sức.', '1000000', '819000', 0, 0, 40, 'noicomdiensharp.jpg'),
(37, 4, 'Điện Thoại Samsung Galaxy A71', 'Màn hình: Super AMOLED, 6.7\", Full HD+\r\nHệ điều hành: Android 10\r\nCamera sau: Chính 64 MP & Phụ 12 MP, 5 MP, 5 MP\r\nCamera trước: 32 MP\r\nCPU: Snapdragon 730 8 nhân\r\nRAM: 8 GB\r\nBộ nhớ trong: 128 GB\r\nThẻ nhớ: MicroSD, hỗ trợ tối đa 512 GB\r\nThẻ SIM: 2 Nano SIM, Hỗ trợ 4G\r\nDung lượng pin: 4500 mAh, có sạc nhanh\r\nThương hiệu: SAMSUNG\r\nBộ sản phẩm bao gồm: Hộp máy, điện thoại, củ sạc, tai nghe, que chọc sim và sách hướng dẫn sử dụng.\r\nThông tin bảo hành:\r\nBảo hành chính hãng 12 tháng. ', 'Màn hình lớn, trải nghiệm tràn viền cực đã\r\nGalaxy A71 sở hữu màn hình tràn viền Infinity-O với rãnh camera được đặt chính giữa tương tự như trên flagship Note 10. Điểm cộng là phần rãnh camera nay đã được làm rất nhỏ, ít gây chú ý, cho trải nghiệm hình ảnh thoải mái và ít bị phân tâm hơn.\r\nKích thước màn hình lên đến 6.7 inch, được \"phóng lớn\" so với Galaxy A51 (6.5 inch), cho không gian trải nghiệm thoải mái và đã mắt.Tấm nền Super AMOLED độ phân giải Full HD+ mang lại độ tương phản tốt, màu đen sâu và hình ảnh được tái hiện rực rỡ, nịnh mắt hơn.\r\nThiết bị cũng sở hữu cảm biến vân tay quang học được đặt ngay dưới màn hình theo xu hướng hiện nay. Vị trí đặt vân tay quen thuộc và thuận tiện để thao tác.\r\nDẫn đầu xu hướng thiết kế 2020\r\nTổng thể Galaxy A71 toát lên vẻ sang trọng và thời thượng. Điện thoại sở hữu mặt lưng bóng bẩy với họa tiết vân hình kim cương nổi bật phía sau. Hiệu ứng chuyển màu cũng được làm rất vừa phải và tinh tế.\r\nCụm camera sau của A71 được sắp xếp theo hình chữ L, đặt gọn gàng trong ô chữ nhật lớn nổi bật. Đây gần như sẽ là phong cách thiết kế chủ đạo của Samsung trong năm 2020.\r\nBộ 4 camera chuyên nghiệp, camera macro đột phá\r\nMột trong những điểm nhấn lớn nhất của Galaxy A71 chính là camera. Cụ thể, điện thoại này được trang bị hệ thống 4 camera sau với sự xuất hiện của camera Macro chụp cận cảnh đột phá.\r\nCamera macro 5 MP hoàn toàn mới, cho phép bạn đến thật gần để chụp được các vật thể tí hon hay chụp cận cảnh đặc tả các phần của những chủ thể lớn. Ví dụ như các bức ảnh gân lá nghệ thuật, cận cảnh các chi tiết của thiết bị công nghệ hay \"soi kĩ\" các đồ vật trang trí trong nhà.\r\nCamera chính 64 MP, nhờ khẩu độ lớn nên A71 sẽ có khả năng chụp thiếu sáng tốt hơn, cho mọi bức hình thêm phần rõ ràng và sắc sảo.\r\nỐng kính 12 MP cho góc chụp siêu rộng, Galaxy A71 dễ dàng ghi lại khung cảnh hùng vĩ của núi đồi hay các bức ảnh tập thể đông người.\r\nMáy cũng có khả năng chụp xoá phông chuyên nghiệp với camera cảm biến chiều sâu 5 MP, giúp làm mờ hậu cảnh chính xác, tách bạch hơn để làm nổi bật chủ thể của bức hình.\r\nHiệu năng mạnh mẽ, lưu trữ thoải mái\r\nSamsung Galaxy A71 sử dụng chip Qualcomm Snapdragon 730 - một con chip cận cao cấp, vẫn đáp ứng được tốt hiệu năng xử lý các công việc hàng ngày, chơi mượt các game phổ biến như PUBG, Liên Quân.\r\nDung lượng RAM 8 GB cho phép máy hoạt động trơn tru kể cả khi dùng nhiều ứng dụng cùng lúc, vừa đọc báo vừa nhắn tin, mở nhiều tab trình duyệt... A71 sở hữu bộ nhớ 128 GB - cho bạn không gian lưu trữ thoải mái trên điện thoại, cài đặt nhiều ứng dụng không lo bị đầy.\r\nHệ điều hành cao cấp trên sản phẩm tầm trung\r\nSamsung Galaxy A71 ra mắt chạy hệ điều hành Android 10 với giao diện người dùng OneUI 2.0. Samsung nói rằng họ đã tối ưu phần mềm theo hướng đề cao sự tiện lợi, giúp khách hàng có thể sử dụng dễ dàng bằng một tay và thiết kế tinh gọn, nhẹ nhàng.', '10490000', '7290000', 0, 0, 40, 'dienthoaisamsung.jpg'),
(38, 4, 'Điện thoại OPPO Reno5', 'Công nghệ màn hình: AMOLED;\r\nĐộ phân giải: Full HD+ (1080 x 2400 Pixels)\r\nKích thước màn hình: 6.43\";\r\nTần số quét: 90 Hz;\r\nĐộ sáng tối đa: 600 nits;\r\nMặt kính cảm ứng: Kính cường lực Corning Gorilla Glass 3+;\r\nHệ điều hành: Android 11;\r\nChip xử lý (CPU): Snapdragon 720G;\r\nTốc độ CPU: 2 nhân 2.3 Ghz & 6 nhân 1.8 Ghz;\r\nChip đồ họa (GPU): Adreno 618;\r\nRAM: 8 GB;\r\nBộ nhớ trong: 128 GB;\r\nBộ nhớ còn lại (khả dụng) khoảng: 115 GB;\r\nThẻ nhớ: MicroSD, hỗ trợ tối đa 256 GB;\r\nDanh bạ: Không giới hạn;\r\nThiết kế: Nguyên khối;\r\nChất liệu: Khung nhựa phủ sơn kim loại & Mặt lưng thuỷ tinh hữu cơ;\r\nKích thước: Dài 159.1 mm - Ngang 73.4 mm - Dày 7.7 mm - Nặng 171 g;\r\nThời điểm ra mắt: 12/2020.', 'OPPO Reno5 là sự kết hợp đầy ấn tượng giữa hiệu năng và thiết kế, mang đến cho người dùng một chiếc điện thoại tích hợp nhiều công nghệ camera, sạc pin hàng đầu của OPPO trong một mức giá tầm trung.\r\nCó thể nói OPPO Reno5 là mẫu điện thoại phô diễn được đỉnh cao thiết kế và công nghệ chế tác của OPPO khi bề mặt lưng được phủ lớp Reno Glow với ngàn tinh thể phát sáng siêu nhỏ tạo nên hiệu ứng chuyển sắc vô cùng hút mắt.\r\nMặt trước OPPO Reno5 cũng ấn tượng không kém với màn hình tràn viền được làm cong nhẹ 2.5D. Hai bên cạnh viền siêu mỏng, thiết kế camera đục lỗ giúp không gian hiển thị được mở rộng tối đa.\r\nCác góc cạnh của OPPO Reno5 cũng được bo cong mềm mại về 2 phía bên cạnh của máy giúp máy nhìn trông mỏng hơn và tạo sự dễ chịu mỗi khi cầm sử dụng.\r\nReno5 được OPPO ưu ái trang bị vi xử lý Snapdragon 720G, hậu tố “G” phía sau Snapdragon 720 ý muốn nói đây là dòng chip chuyên game đi kèm tính năng Elite Gaming của Qualcomm.\r\nKhông phải bàn về hiệu năng của Snapdragon 720G, khi đây là vi xử lý chuyên chơi game với những nâng cấp ấn tượng về xung nhịp lẫn khả năng đồ họa, hứa hẹn mang đến trải nghiệm game đỉnh cao.\r\nBên cạnh đó, OPPO Reno5 còn chiếm ưu thế về lưu trữ và đa nhiệm khi trang bị RAM 8 GB tốc độ LPDDR4x cùng bộ nhớ trong 128 GB UFS 2.1, OPPO Reno5 vẫn dư sức cho bạn thoải mái cài đặt ứng dụng, lưu trữ ảnh, nhạc, video, đáp ứng đủ nhu cầu lưu trữ của bạn.\r\nMàn hình chính là điểm cộng giúp cho Reno5 vượt trội trong tầm giá, khi tích hợp tần số quét 90 Hz và tốc độ lấy mẫu cảm ứng 180 Hz, mang đến độ mượt mà trong trải nghiệm vuốt chạm hàng ngày và cảm ứng siêu nhạy không độ trễ khi chơi game.\r\nSở hữu màn hình rộng cùng tấm nền AMOLED Full HD+ sắc nét, OPPO Reno5 mang đến trải nghiệm đẳng cấp với màu sắc sống động, độ sáng cao, màu đen sâu thu hút bạn tập trung vào nội dung giải trí.\r\nOPPO Reno5 dùng chung hệ thống cảm biến cao cấp trên OPPO Reno5 Pro với cụm 4 camera sau gồm camera chính 64 MP, camera góc siêu rộng 8 MP, camera macro 2 MP và camera xóa phông 2 MP hứa hẹn đem đến chất lượng ảnh vô cùng ấn tượng.\r\nCảm biến 64 MP cùng các camera phụ cung cấp nhiều tùy chọn chụp đa dạng từ chụp cận cảnh đến góc rộng, xóa phông dù ngày hay đêm, thỏa mãn đam mê nhiếp ảnh của bạn.\r\nGiờ đây, Reno5 đã có thể quay phim HDR, bạn có thể quay phim khi ngược sáng mà các chi tiết vẫn được giữ lại đầy đủ cùng cân bằng trắng được thể hiện tự nhiên giữa các vùng chênh sáng. \r\nCùng với đó là công nghệ siêu chống rung 3.0, chế độ quay đồng thời từ cả camera trước và sau, chỉnh sửa video dễ dàng vớiSoloop giúp bạn sáng tạo những nội dung vlog thú vị để chia sẻ với bạn bè.\r\nCực phẩm camera trước 44 MP của OPPO sẽ làm hài lòng các tín đồ chuyên chụp ảnh selfie bởi tính năng làm đẹp AI vô cùng tự nhiên, không cần đến ứng dụng chụp hình nào khác vì OPPO Reno5 đã sở hữu camera selfie tốt nhất hiện nay.\r\nSạc nhanh 50 W nhanh đến ấn tượng: trang bị viên pin dung lượng 4310 mAh, OPPO Reno5 cho bạn một ngày làm việc, trải nghiệm hay giải trí mà không cần lo nghĩ về pin.\r\nColorOS đầy màu sắc, đa tính năng, lướt mượt mà: OPPO Reno5 được cài sẵn ColorOS 11.1 mới nhất cùng với phiên bản Android 11 vừa được ra mắt gần đây, mang đến tính năng thú vị như chế độ Dark Mode, trợ lý ảo Breeno, hay thư giãn cùng OPPO Relax 2.0, cho bạn tận hưởng những âm thanh thường ngày.\r\n\r\n', '8690000', '8490000', 0, 0, 37, 'dienthoaioppo.jpg'),
(39, 4, 'Điện thoại Xiaomi Mi 11 Lite', 'Công nghệ màn hình: AMOLED;\r\nĐộ phân giải: Full HD+ (1080 x 2400 Pixels);\r\nKích thước màn hình: 6.55\";\r\nTần số quét: 90 Hz;\r\nĐộ sáng tối đa: 500 nits;\r\nMặt kính cảm ứng: Kính cường lực Corning Gorilla Glass 5;\r\nHệ điều hành: Android 11;\r\nChip xử lý (CPU): Snapdragon 732G;\r\nTốc độ CPU: 2 nhân 2.3 Ghz & 6 nhân 1.8 Ghz;\r\nChip đồ họa (GPU): Adreno 618;\r\nRAM: 8 GB;\r\nBộ nhớ trong: 128 GB;\r\nBộ nhớ còn lại (khả dụng) khoảng: 115 GB;\r\nThẻ nhớ: MicroSD, hỗ trợ tối đa 1 TB;\r\nDanh bạ: Không giới hạn;\r\nDung lượng pin: 4250 mAh;\r\nLoại pin: Li-Po;\r\nHỗ trợ sạc tối đa: 33 W;\r\nSạc kèm theo máy: 33 W;\r\nCông nghệ pin: Sạc pin nhanhTiết kiệm pin;\r\nThiết kế: Nguyên khối;\r\nChất liệu: Khung nhựa & Mặt lưng kính;\r\nKích thước: Dài 160.53 mm - Ngang 75.72 mm - Dày 6.99 mm - Nặng 157 g;\r\nThời điểm ra mắt: 04/2021.', 'Xiaomi Mi 11 Lite là phiên bản thu gọn của Xiaomi Mi 11 5G được ra mắt trước đó. Thừa hưởng nhiều ưu điểm của đàn anh, Mi 11 Lite hoàn toàn có thể đáp ứng tốt các tác vụ thông thường một cách dễ dàng và đặc biệt hơn máy có thiết kế vô cùng mỏng nhẹ và thời trang.\r\nHiệu năng vượt trội với Snapdragon 732G\r\nThoạt nhìn qua Mi 11 Lite thì bạn sẽ ấn tượng với thiết kế vô cùng mỏng, chỉ 6.81 mm và nặng 157 g cho trải nghiệm cầm nằm của bạn vô cùng thoải mái, hạn chế gây ra cảm giác mỏi tay trong quá trình sử dụng.\r\nKích thước các viền xung quanh màn hình chỉ 1.88 mm siêu mỏng, cho kích thước màn hình tối ưu hơn, trải nghiệm xem được rộng mở, thoải mái hơn.\r\nCụm camera sau gần như phẳng so với mặt lưng khi chỉ nhô ra 1.77 mm, mang đến một cái nhìn cân đối, hài hoà.\r\nMi 11 Lite sở hữu cụm 3 camera sau gồm: Camera chính 64 MP, camera góc siêu rộng 8 MP và camera telemacro 5 MP giúp bạn bắt trọn mọi khoảnh khắc cuộc sống bằng những hình ảnh, video chất lượng cao qua nhiều góc nhìn độc đáo.\r\nMàn hình AMOED 6.55 inch có độ tương phản cao, phân định rõ ràng giữa vùng tối và vùng sáng cho khả năng hiển thị chân thực hơn. Độ phân giải Full HD+ mang lại hình ảnh rõ nét, mịn màng hơn.\r\n\r\n\r\n\r\n', '7990000', '7590000', 0, 0, 43, 'dienthoaixiaomi.jpg'),
(40, 4, 'Điện thoại iPhone 12 PM', 'Hãng sản xuất: Apple;\r\nKích thước: 160.8 x 78.1 x 7.4 mm;\r\nTrọng lượng: 228;\r\nBộ nhớ trong: 128/256/512 GB;\r\nLoại SIM: Nano-SIM;\r\nLoại màn hình: Super Retina XDR OLED, HDR10, Dolby Vision, Wide color gamut, True-tone;\r\nKích thước màn hình: 6.7 inches;\r\nĐộ phân giải màn hình: 1284 x 2778 pixels\r\nHệ điều hành: iOS;\r\nPhiên bản hệ điều hành: iOS 14;\r\nChipset: Apple A14 Bionic (5 nm);\r\nCPU: Hexa-core;\r\nGPU: pple GPU (4-core graphics);\r\nKhe cắm thẻ nhớ: Không;\r\nCamera sau: 12 MP, f/1.6, 26mm (wide), 1.4µm, dual pixel PDAF, OIS;\r\n12 MP, f/2.0, 52mm (telephoto), 1/3.4\", 1.0µm, PDAF, OIS, 2x optical zoom;\r\n12 MP, f/2.4, 120˚, 13mm (ultrawide), 1/3.6\"\r\nTOF 3D LiDAR scanner (depth);\r\nCamera trước	: 12 MP, f/2.2, 23mm (wide), 1/3.6\"\r\nSL 3D, (depth/biometrics sensor);\r\nQuay video: Trước: 4K@24/30/60fps, 1080p@30/60/120fps, gyro-EIS;\r\nSau: 4K@24/30/60fps, 1080p@30/60/120/240fps, HDR, Dolby Vision HDR (up to 60fps), stereo sound rec;\r\n3G: HSPA 42.2/5.76 Mbps, EV-DO Rev.A 3.1 Mbps;\r\n4G: LTE-A (4CA) Cat16 1024/150 Mbps;\r\n5G: mmWave, Sub-6 GHz;\r\nWLAN: Wi-Fi 802.11 a/b/g/n/ac/6, dual-band, hotspot;\r\nBluetooth: 5.0, A2DP, LE\r\nGPS: A-GPS, GLONASS, GALILEO, QZSS;\r\nNFC: Yes;\r\nUSB: Lightning, USB 2.0;\r\nCảm biến: Face ID, cảm biến gia tốc, cảm biến tiệm cận, con quay hồi chuyển, cảm biên sáng;\r\nPin; Li-Ion, sạc nhanh 18W, sạc không dây 15W, USB Power Delivery 2.0.', 'iPhone 12 Pro Max 512GB sản phẩm cao cấp nhất trong loạt iPhone 12 Series với mức dung lượng cao nhất cùng nhiều tính năng ưu việt mang đến một trải nghiệm đỉnh cao cho người sở hữu.\r\n\r\nViền máy vuông vức sắc sảo\r\nThiết kế của iPhone 12 Pro Max 512GB nổi bật hơn sản phẩm tiền nhiệm với viền thép vuông vức ánh kim tạo cảm giác mạnh mẽ. Tuy nhiên thiết kế góc cạnh cộng với thân máy lớn nên có thể hơi cấn tay khi cầm.\r\n\r\nLưng máy vẫn là cụm camera “bếp lò” hình vuông, mặt kính mờ chống hạn chế bám bụi và vân tay giống với iPhone 11 Pro Max, có thể thấy iPhone 12 Pro Max không thay đổi nhiều về ngoại hình vì anh em của nó đã khá ổn từ trước.\r\nMàn hình 6.7 inch hiển thị xuất sắc\r\niPhone 12 Pro Max có màn hình lớn 6.7 inch, nhỉnh hơn một chút so với iPhone 11 Pro Max (6.5 inch) cho bạn trải nghiệm xem phim đã mắt hay chơi game tập trung hơn.\r\n\r\niPhone 12 Pro Max vẫn sử dụng màn hình OLED và công nghệ Super Retina như cũ, độ phân giải 2778 x 1284 pixels cho hình ảnh sắc nét hoàn hảo. Hơn nữa thì độ sáng màn hình lên đến 12000 nits ở chế độ HDR cực kì dễ nhìn cho bạn khi sử dụng ngoài trời nắng.\r\nCải tiến hơn với 4 camera tối ưu chụp đêm\r\nNgoài “3 viên trân châu” 12MP cực phẩm: 1 camera chính góc rộng, 1 camera góc siêu rộng 120 độ và 1 camera Tele; iPhone 12 Pro Max năm nay được bổ sung thêm 1 camera LIDAR Scanner hỗ trợ lấy nét nhanh gấp 6 lần trong điều kiện thiếu sáng, hỗ trợ công nghệ AR, 3D,...\r\n\r\n Cải tiến hơn với 4 camera tối ưu chụp đêm\r\nNgoài “3 viên trân châu” 12MP cực phẩm: 1 camera chính góc rộng, 1 camera góc siêu rộng 120 độ và 1 camera Tele; iPhone 12 Pro Max năm nay được bổ sung thêm 1 camera LIDAR Scanner hỗ trợ lấy nét nhanh gấp 6 lần trong điều kiện thiếu sáng, hỗ trợ công nghệ AR, 3D,...\r\n\r\n Chip A14 mạnh mẽ\r\niPhone 12 Pro Max sử dụng CPU Apple A14 Bionic sản xuất trên tiến trình 5nm hiện đại đồng nghĩa sẽ tăng hiệu suất để có tuổi thọ pin tốt hơn thế hệ trước. Machine Learning được tăng lên gấp 10 lần, qua đó cải thiện hình ảnh trong từng cảnh phim hay đồ họa gaming.\r\n\r\nSản phẩm có dung lượng 128GB - phiên bản tiết kiệm trong dòng Pro Max đủ cho bạn lưu trữ trên 1000 hình ảnh, video cùng nhiều ứng dụng hữu ích.\r\nHỗ trợ sạc không dây - sạc nhanh\r\niPhone 12 Pro Max dung lương 128GB này có viên pin liền dung lượng lớn, thời gian sử dụng ước tính khoảng 8 tới 10 tiếng sau 1 lần sạc đầy. Máy còn được hỗ trợ sạc không dây 15W, sạc nhanh 50% trong vòng 30 phút.', '43990000', '36850000', 0, 0, 35, 'dienthoaiiphone.jpg'),
(41, 7, 'Quạt Đứng ASIA D16021-GREY', '-Loại Sản Phẩm: Quạt Đứng;\r\n-Số Cánh Quạt: 3 cánh;\r\nChế Độ Gió: Gió thường;\r\nTốc Độ Gió: 3 tốc độ;\r\nLưu Lượng Gió: 69 M3/Phút;\r\nHẹn Giờ: Không;\r\nĐiều khiển Từ Xa: Không;\r\nCông Suất: Dưới 100W;\r\nCông Suất Thực: 45W;\r\nHãng Sản Xuất: ASIA;\r\nSản Xuất Tại: Việt Nam;\r\nBảo Hành Tại Siêu Thị: 12 tháng.', 'Quạt đứng ASIA D16021-GREY tạo ra một không gian hoàn toàn mới lạ trong ngôi nhà của bạn với màu vàng xám trọng và kiểu dáng hiện đại. Cánh quạt được làm bằng kim loại cao cấp, vừa tăng độ bền vừa nâng cao được khả năng làm mát. Quạt được làm từ chất liệu nhựa cao cấp, sáng bóng, hạn chế bám bụi bẩn và cực kỳ bền chắc. Quạt là một cách tuyệt vời để làm mát, lưu thông không khí trong nhà của bạn.\r\nChân đế của quạt thiết kế chắc chắn giúp quạt đứng vững vàng không bị chông chênh trên mặt phẳng. Sản phẩm được trang bị ba chế độ làm mát: mạnh, vừa, nhẹ khác nhau nên bạn có thể dễ dàng tùy chọn phụ thuộc vào nhu cầu người sử dụng. Trong quá trình vận hành, quạt hoạt động êm ái không tạo ra tiếng ồn lớn gây ảnh hưởng đến mọi người, mang lại sự thoải mái. Khung lưới bảo vệ của thiết bị giúp người dùng không bị cánh quạt cắt, rất an toàn đặc biệt là trẻ em.', '600000', '489000', 0, 0, 24, 'quatmayasia.png');
INSERT INTO `tbl_sanpham` (`sanpham_id`, `category_id`, `sanpham_name`, `sanpham_chitiet`, `sanpham_mota`, `sanpham_gia`, `sanpham_giakhuyenmai`, `sanpham_active`, `sanpham_hot`, `sanpham_soluong`, `sanpham_image`) VALUES
(42, 7, 'Quạt lửng Senko L1638', 'Công suất: 47W;\r\nĐường kính cánh quạt: 39cm;\r\nNguồn điện áp: 220V/50Hz;\r\nChất liệu cánh quạt: Nhựa;\r\nSố cánh quạt: 3 cánh;\r\nTốc độ gió: 3 tốc độ;\r\nLưu lượng gió: 64.4 m3/min;\r\nĐiều chỉnh chiều cao: Có;\r\nChiều cao quạt: 77cm - 95cm;\r\nTrọng lượng sản phẩm: 3,4kg;\r\nThương hiệu: Việt Nam;\r\nSản xuất tại: Việt Nam;\r\nBảo hành: 24 tháng.', 'Quạt lửng Senko L1638 thường được sử dụng tại các gia đình, văn phòng,...nhờ ưu điểm làm mát nhanh và diện tích rộng của mình.\r\nQuạt lửng được làm từ chất liệu nhựa cao cấp có ưu điểm bền bỉ, dẻo dai và dễ dàng lau chùi vệ sinh. \r\nThân quạt dạng đứng có thể thay đổi độ cao trong khoảng 77cm - 95cm.\r\nCánh quản bản to có thể chịu được tốc độ quay lớn, tạo ra luồng gió mát. Bạn có thể tháo lắp cánh quạt một cách dễ dàng để vệ sinh khi cần thiết. \r\nLồng quạt với các nan nhỏ giúp đảm bảo an toàn trong quá trình quạt vận hành, đặc biệt đối với gia đình có trẻ nhỏ.\r\nChân đế thiết kế bản rộng giúp quạt đứng vững chắc trên nhiều chất liệu mặt phẳng khác nhau.\r\nQuạt Senko hiện có 3 tốc độ khác nhau được điều chỉnh bằng nút bấm ngay trên chân đế. Lưu lượng gió lớn nhất của quạt là 64.4 m3/min, bạn có thể để quạt quay 180 độ liên tục từ trái qua phải và ngược lại, giúp làm mát diện tích lớn hơn. Công suất hoạt động của quạt là 47W. \r\nQuạt lửng Senko L1638 có thiết kế đơn giản, màu sắc tươi sáng cho luồng gió ổn định, là thiết bị điện gia dụng không thể thiếu trong những ngày hè oi nóng. ', '390000', '360000', 0, 0, 30, 'quatmaysenko.jpg'),
(43, 7, 'Quạt treo Lifan TE-1689', 'Loại quạt: Quạt treo tường;\r\nCông suất: 50W;\r\nTốc độ gió: 3 mức gió;\r\nĐường kính cánh quạt: 40 cm;\r\nSố cánh quạt: 3 cánh;\r\nChế độ gió: Gió thường;\r\nLoại motor: Bạc thau;\r\nTiện ích: Hẹn giờ tắt;\r\nKích thước: Ngang 48 cm - Cao 48 cm - Sâu 35 cm;\r\nTrọng lượng: 3.1 kg;\r\nThương hiệu của: Việt Nam;\r\nSản xuất tại: Việt Nam.', 'Quạt Lifan thiết kế dạng treo tường, tiết kiệm không gian, sử dụng tiện lợi.\r\nQuạt có sải cánh dài 40 cm, động cơ bạc thau chạy êm ái, hiệu suất làm mát cao.\r\nChế độ hẹn giờ linh hoạt với thời gian hẹn tối đa lên đến 7.5 tiếng\r\nQuạt treo tường có kiểu dáng hiện đại, thiết kế treo tường tiết kiệm diện tích \r\nLồng quạt đan khít rất an toàn cho người dùng, chất liệu cao cấp ít bám bụi, dễ vệ sinh\r\nBa cánh quạt có đường kính 40 cm tản gió rộng làm mát hiệu quả ở không gian lớn\r\nQuạt có 3 tốc độ gió tùy chỉnh theo những nhu cầu làm mát khác nhau\r\n Điều khiển từ xa tiện lợi, không cần phải di chuyển tới gần để điều chỉnh các chức năng của quạt\r\nChức năng hẹn giờ thông minh giúp bạn chủ động được thời gian sử dụng quạt\r\nQuạt treo tường Lifan TE-1689 thương hiệu Lifan nổi tiếng, phù hợp với nhiều không gian sử dụng cùng nhiều chức năng hiện đại, là sự lựa chọn hàng đầu cho nhu cầu làm mát của mọi gia đình.', '360000', '330000', 0, 0, 32, 'quatmaylifan.jpg'),
(44, 7, 'Quạt Treo Tường PANASONIC F409M', 'Loại Sản Phẩm: Quạt Treo;\r\nDung Tích: Khác;\r\nSố Cánh Quạt: 3 cánh;\r\nTốc Độ Gió: 3 tốc độ;\r\nLưu Lượng Gió: 68.2m3/phút;\r\nHẹn Giờ; 6h tắt;\r\nĐiều khiển Từ Xa: Có;\r\nMàn Hình Hiển Thị: Điện tử;\r\nDiện Tích Làm Mát: Không;\r\nChất Liệu: Nhựa (Lồng sắt);\r\nMàu Sắc: Nhiều màu;\r\nKích thước Cánh Quạt: 400 mm;\r\nCông Suất Thực: 51w;\r\nHãng Sản Xuất: PANASONIC (Thương hiệu: Nhật);\r\nSản Xuất Tại: Malaysia;\r\nBảo Hành Tại Siêu Thị: 12 tháng.', 'Kiểu dáng sang trọng, thiết kế treo tường tiết kiệm không gian .3 cánh quạt lớn với công suất 55 W có khả năng làm mát nhanh trên diện tích rộng.\r\nChức năng hẹn giờ hữu ích giúp bạn chủ động được thời gian sử dụng.3 mức gió tùy chỉnh theo những nhu cầu làm mát khác nhau\r\n\r\n', '2640000', '1990000', 0, 0, 41, 'quatmaypanasonic.jpg'),
(45, 8, 'Máy Lạnh Casper 1.0 HP SC-09TL32', 'Công suất Làm lạnh (BTU): 9.000 BTU;\r\nCông suất lạnh (HP): 1.0 Hp;\r\nInverter-Tiết kiệm điện: Không;\r\nThể tích phòng sử dụng: Dưới 45 m3;\r\nNhãn năng lượng tiết kiệm điện: 1 sao ;(Hiệu suất năng lượng 3.11);\r\nHướng không khíLên/Xuống: Có;\r\nNguồn điện: 220V/50Hz/1.5A;\r\nTiện ích:	Chế độ Hẹn giờ tự động bật/tắt, Chế độ Tự khởi động lại sau khi có điện, Cơ chế chống nấm mốc Anti-Fungusproof; Chế độ TURBO làm lạnh/Làm nóng nhanh; Lưới lọc Airfresh phát huy tối đa khả năng làm sạch; Cánh đảo gió hình vây phân bổ tối ưu luồng không khí tươi mới;\r\nLàm lạnh nhanh: TURBO;\r\nBật/tắt màn hình hiển thị: Có;\r\nBật/tắt tiếng bíp: Có;\r\nChế độ quạt: Tự động hoặc chỉnh tay;\r\nLoại gas: R-32;\r\nKính thước dàn lạnh: 792×292×201mm;\r\nKích thước dàn nóng: 660×500×240mm;\r\nTrọng lượng dàn lạnh (Kg): 8 kg;\r\nrọng lượng dàn nóng(Kg): 24 kg;\r\nHãng sản xuất: CASPER;\r\nSản xuất tại: Thái Lan;\r\nBảo hành: 36 tháng;\r\nNăm ra mắt: 2020;\r\nChiều dài lắp đặt ống đồng: Tối thiểu 3 m\r\nNếu dùng cho hoạt động kinh doanh (giặt ủi, nhà hàng, khách sạn...): Không bảo hành.', 'Dàn lạnh điều hòa Casper được thiết kế nguyên khối giúp tăng tính thẩm mỹ cũng như tăng độ bền trong thiết kế.\r\nChức năng iClean - tự làm sạch thông minh giúp ngăn ngừa sự hình thành vi khuẩn, nấm mốc trong dàn lạnh, mang đến không gian sống trong lành hơn cho người sử dụng. Khi kích hoạt chức năng iClean, máy sẽ làm lạnh và làm đóng băng bề mặt dàn lạnh. Sau đó, máy kích hoạt làm nóng, làm tan băng nhờ đó loại bỏ bụi bẩn bề mặt dàn.\r\nĐiều hòa Casper được trang bị động cơ quạt tiên tiến có hiệu năng cao và giảm tiếng ồn thấp nhất có thể, giúp bạn tận hưởng không gian yên tĩnh tuyệt đối.\r\nVới ưu thế về việc ứng dụng nhiều công nghệ hiện đại vào sản phẩm, Casper đã phát triển môi chất lạnh R32 – môi chất tân tiến nhất hiện nay cho dòng sản phẩm CONVERT Series. R32 có những ưu thế vượt trội: Khả năng làm lạnh sâu, tiết kiệm năng lượng tiêu thụ, thân thiện với môi trường…', '6290000', '4790000', 0, 0, 18, 'MAYLANHCASPER.jpg'),
(46, 8, 'Máy Lạnh AQUA Inverter 1.0 HP AQA-KCRV10WNMA', 'Công suất Làm lạnh (BTU): 9.100 BTU;\r\nCông suất lạnh (HP): 1.0 Hp;\r\nInverter-Tiết kiệm điện: Có;\r\nThể tích phòng sử dụng: Dưới 45 m3;\r\nNhãn năng lượng tiết kiệm điện: 5 sao ;(Hiệu suất năng lượng 4.9);\r\nHướng không khí Lên/Xuống: Có;\r\nBộ lọc khử mùi: Chỉ có lưới lọc bụi thô;\r\nỐng đồng: 6/10;\r\nCông suất tiêu thụ trung bình: 750 W;\r\nNguồn điện: 220V/50Hz;\r\nTiện ích:	Chế độ tiết kiệm điện Inverter, Chức năng tự làm sạch AQUA FRESH, Luồng gió 3D, Chế độ siêu tĩnh, Lớp chống ăn mòn Bluefin...;\r\nLàm lạnh nhanh: Có;\r\nBật/tắt tiếng bíp: Có;\r\nChế độ quạt: Tự động hoặc chỉnh tay\r\nLoại gas: R-32;\r\nKính thước dàn lạnh: C x R x S: 280 x 810 x 200mm;\r\nKích thước dàn nóng: C x R x S: 540 x 780 x 245mm;\r\nTrọng lượng dàn lạnh (Kg): 9 Kg;\r\nTrọng lượng dàn nóng(Kg): 25 Kg;\r\nHãng sản xuất: AQua;\r\nSản xuất tại: Việt Nam;\r\nBảo hành: 24 tháng;\r\nNăm ra mắt: 2021;\r\nChiều dài lắp đặt ống đồng: Tối thiểu 3 m\r\nNếu dùng cho hoạt động kinh doanh (giặt ủi, nhà hàng, khách sạn...): Không bảo hành.', 'Máy lạnh AQUA Inverter 1.0 HP AQA-KCRV10WNMA được thiết kế hiện đại, sắc trang nhã, đường nét tinh tế sẽ làm nổi bật lên nội thất không gian nhà bạn. Với công suất 1 HP, máy lạnh AQUA đáp ứng tốt nhu cầu làm mát cho căn phòng nhỏ có diện tích dưới 15m2.\r\nKhi chế độ Turbo được kích hoạt, máy lạnh AQUA Inverter hoạt động ở mức công suất cao và thổi gió mạnh hơn giúp căn phòng nhanh chóng được đạt đến nhiệt độ cài đặt chỉ trong thời gian ngắn.\r\nCác lá nhôm tản nhiệt ở dàn lạnh và dàn nóng được phủ lớp chống ăn mòn Bluefin không chỉ giúp máy vận hành bền bỉ, tuổi thọ dài lâu mà còn tăng cường khả năng trao đổi nhiệt, góp phần tiết kiệm năng lượng.\r\nTrang bị luồng gió thổi 3D với cánh đảo gió có thể đảo được theo chiều dọc (lên – xuống) và chiều ngang (trái – phải), máy lạnh AQUA 1.0 HP mang hơi lạnh lan tỏa nhanh chóng, đồng đều đến từng ngóc ngách trong phòng.\r\nMáy lạnh sử dụng gas R32 góp phần giảm tiêu thụ điện năng hơn so với loại gas cũ, thân thiện với môi trường, an toàn hơn trong quá trình sử dụng.\r\nChiếc máy lạnh 1 HP này được trang bị công nghệ Inverter vừa nâng cao khả năng tiết kiệm điện vừa duy trì ổn định nhiệt độ như mức cài đặt, vận hành êm ái cho bạn không gian dễ chịu thoải mái làm việc hay học tập.\r\nBên cạnh đó, chế độ tiết kiệm điện Inverter trên máy lạnh giúp bạn tiết kiệm chi phí tiền điện mỗi tháng nhưng vẫn đảm bảo hiệu quả làm lạnh tối ưu.\r\nVới chế độ siêu tĩnh, máy lạnh sẽ thổi ra luồng khí nhẹ nhàng, dễ chịu, hoạt động êm ái, giúp bạn nhanh chìm vào giấc ngủ ngon và sâu sau một ngày làm việc vất vả.\r\n\r\n', '8490000', '7990000', 0, 0, 24, 'MAYLANHAQUA.png'),
(47, 8, 'Máy Lạnh SHARP Inverter 1.0 HP AH/AU-X9XEW', 'Công suất Làm lạnh (BTU): 9.000 BTU;\r\nCông suất lạnh (HP): 1.0 Hp;\r\nInverter-Tiết kiệm điện: Có;\r\nThể tích phòng sử dụng: Dưới 45 m3;\r\nNhãn năng lượng tiết kiệm điện:	5 sao; (Hiệu suất năng lượng 4.93);\r\nHướng không khí Lên/Xuống: Có;\r\nBộ lọc khử mùi: Lưới bụi polypropylene;\r\nỐng đồng: 6/10;\r\nCông suất tiêu thụ trung bình: 0.8 kW/h;\r\nNguồn điện: 220V/50Hz/1.5A;\r\nTiện ích:	Thổi gió dễ chịu (cho trẻ em, người già), Hẹn giờ bật tắt máy, Làm lạnh nhanh tức thì, Tự khởi động lại khi có điện;\r\nLàm lạnh nhanh: Powerful Jet;\r\nBật/tắt màn hình hiển thị: Có;\r\nBật/tắt tiếng bíp: Có;\r\nChế độ quạt: Tự động hoặc chỉnh tay\r\nLoại gas: R-32;\r\nTrọng lượng dàn lạnh (Kg): 8 kg;\r\nTrọng lượng dàn nóng(Kg): 18 kg;\r\nHãng sản xuất: Sharp (Thương Hiệu: Nhật Bản);\r\nSản xuất tại: Thái Lan;\r\nBảo hành: 12 tháng;\r\nNăm ra mắt: 2020;\r\nChiều dài lắp đặt ống đồng: Tối thiểu 3 m\r\nNếu dùng cho hoạt động kinh doanh (giặt ủi, nhà hàng, khách sạn...): Không bảo hành.', 'Máy lạnh Sharp Inverter 1.0 HP AH/AU-X9XEW mang kiểu dáng mạnh mẽ, tinh tế cùng tông màu trắng cực kì sang trọng, thích hợp với mọi không gian nội thất nhà bạn.\r\nVới công suất 1 HP, chiếc máy lạnh này là sự lựa chọn lý tưởng cho phòng dưới 15 m2 như phòng ngủ nhỏ, phòng học hoặc phòng làm việc riêng...\r\nỨng dụng công nghệ J-Tech Inverter, máy lạnh Sharp vận hành êm ái, duy trì nhiệt độ ổn định và điều chỉnh được nhiều cấp độ để đáp ứng nhu cầu làm lạnh hiệu quả hơn. Từ đó, tiết kiệm đến 65% điện năng tiêu thụ so với máy lạnh thường.\r\nMáy lạnh Sharp Inverter 1.0 HP sử dụng gas R32 vừa thân thiện với mới môi trường, an toàn cho sức khỏe, vừa làm lạnh nhanh và sâu hơn. Đặc biệt, chiếc máy lạnh Sharp này có thể chỉnh nhiệt độ thấp xuống tới 14 độ C, gần như thấp nhất so với các hãng máy lạnh khác.\r\nCông nghệ làm lạnh nhanh Powerful Jet giảm ngay 5 độ C trong vòng 5 phút kể từ khi khởi động máy và tốc độ làm lạnh nhanh hơn 28% so với máy lạnh thông thường. Với công nghệ này, bạn có thể tận hưởng không khí mát mẻ, thoải mái ngay khi bước vào phòng mà không phải chờ lâu.\r\nNhờ chức năng hiện giờ bật/tắt máy lạnh, bạn sẽ không phải thức giấc vào lúc nửa đêm để điều chỉnh nhiệt độ, tắt/mở máy do nhiệt độ quá nóng hoặc quá lạnh, đảm bảo giấc ngủ của bạn sâu và ngon hơn.\r\nKhi cúp điện đột ngột, máy lạnh sẽ tự động ghi nhớ các cài đặt hiện tại như nhiệt độ, hướng gió... và khi có điện trở lại, thiết bị sẽ tự điều chỉnh các thông số đã ghi nhớ trước đó mà không cần sự can thiệp từ người dùng.\r\nLưới bụi polypropylene lọc sạch các tác nhân gây hại cho sức khỏe trong không khí như bụi bẩn, phấn hoa, mùi hôi khó chịu…, trả lại bầu không khí trong lành, mát mẻ và an toàn cho sức khỏe gia đình bạn.\r\nCánh đảo gió sẽ hướng luồng gió lên trần nhà giúp làm lạnh đồng đều khắp căn phòng và tránh làm lạnh trực tiếp, tạo cảm giác mát mẻ dễ chịu, sau khi phòng đạt tới nhiệt độ cài đặt mong muốn. Chế độ này rất phù hợp cho gia đình có trẻ em, người già hay những người có cơ thể nhạy cảm với khí lạnh.\r\n', '8990000', '8140000', 0, 0, 28, 'MAYLANHSHARP.jpg'),
(48, 8, 'Máy lạnh Samsung Inverter 1 HP AR10TYHYCWKNSV', 'Công suất Làm lạnh (BTU): 9.400 BTU;\r\nCông suất lạnh (HP): 1.0 Hp;\r\nInverter-Tiết kiệm điện: Có;\r\nThể tích phòng sử dụng: Dưới 45 m3;\r\nNhãn năng lượng tiết kiệm điện:	5 sao (Hiệu suất năng lượng 4.65);\r\nHướng không khí Lên/Xuống: Có;\r\nBộ lọc khử mùi: Lưới lọc bụi bẩn Easy -Filter, Màng lọc kháng khuẩn Ag+\r\nỐng đồng: 6/10;\r\nCông suất tiêu thụ trung bình:: 0.91 kW/h\r\nNguồn điện: 220V/50Hz/1.5A;\r\nTiện ích:	Có tự điều chỉnh nhiệt độ (chế độ ngủ đêm), Chức năng hút ẩm, Hẹn giờ bật tắt máy, Làm lạnh nhanh;\r\nLàm lạnh nhanh: Có;\r\nBật/tắt màn hình hiển thị: Có;\r\nBật/tắt tiếng bíp: Có;\r\nLoại gas	R-32;\r\nKính thước dàn lạnh: 820x 299x 215 mm;\r\nKích thước dàn nóng: 725x 475x 277 mm;\r\nTrọng lượng dàn lạnh (Kg): 8.6 kg;\r\nTrọng lượng dàn nóng(Kg): 19.4 kg;\r\nHãng sản xuất: Samsung (Thương Hiệu: Hàn Quốc);\r\nSản xuất tại: Thái Lan;\r\nBảo hành: 24 tháng;\r\nNăm ra mắt: 2020;\r\nChiều dài lắp đặt ống đồng: Tối thiểu 3 m\r\nNếu dùng cho hoạt động kinh doanh (giặt ủi, nhà hàng, khách sạn...): Không bảo hành.', 'Mang thiết kế sang trọng, gam màu trắng tinh tế, máy lạnh Samsung Inverter 1 HP AR10TYHYCWKNSV sẽ góp phần tăng thêm vẻ hiện đại, thanh lịch cho không gian nội thất nhà bạn.\r\nChiếc máy lạnh Samsung này sở hữu công suất 1 HP có thể đáp ứng tốt nhu cầu làm lạnh cho căn phòng có diện tích dưới 15 m2.\r\nĐộng cơ Digital Inverter Boost tiết kiệm điện hiệu quả, làm lạnh nhanh chóng\r\nĐược trang bị động cơ Digital Inverter Boost, máy lạnh Samsung AR10TYHYCWKNSV có tốc độ làm lạnh nhanh, duy trì ổn định nhiệt độ mong muốn nhưng vẫn tiết kiệm năng lượng điện đáng kể khi hoạt động.\r\nLưới lọc bụi bẩn Easy Filter có thể dễ dàng tháo rời để làm vệ sinh một cách nhanh chóng, góp phần giúp máy lạnh luôn hoạt động hiệu quả, mang đến luồng khí tươi mát cho căn phòng.\r\nMáy lạnh Samsung AR10TYHYCWKNSV trang bị thêm màng lọc kháng khuẩn tinh thể Bạc, có thể loại bỏ vi khuẩn và mùi hôi khó chịu trong suốt quá trình sử dụng máy lạnh.\r\nChế độ hút ẩm có tác dụng hút bớt hơi ẩm trong không khí, đem lại không gian thoáng đãng, khô ráo cho căn phòng, đặc biệt là vào những ngày trời mưa, độ ẩm cao.\r\nMáy lạnh Samsung 1 HP có khả năng tự động ghi nhớ chế độ đã được cài đặt khi cúp điện và tự động khởi động lại sau khi có điện trở lại, người dùng không cần phải tốn thời gian điều chỉnh lại nhiệt độ\r\nKhi kích hoạt chế độ một người dùng, máy lạnh sẽ nhận biết được nơi nào có hoạt động của con người để làm lạnh vùng đó nhiều hơn cũng như hạn chế làm lạnh những vùng không có người, mang lại khả năng làm lạnh hiệu quả nhưng vẫn cực kỳ tiết kiệm điện.\r\nTính năng đảo gió 4 hướng tự động điều chỉnh hướng gió lên, xuống, trái và phải, giúp thổi hơi lạnh lan toả xa và đều khắp phòng mà không cần điều chỉnh cánh đảo gió thủ công như trước.\r\n\r\n\r\n\r\n\r\n', '9090000', '8490000', 0, 0, 32, 'MAYLANHSAMSUNG.jpg');

